<?phpnamespace Hakam\MultiTenancyBundle\Command;use Hakam\MultiTenancyBundle\Services\DbService;use Symfony\Component\Console\Attribute\AsCommand;use Symfony\Component\Console\Command\Command;use Symfony\Component\Console\Input\InputArgument;use Symfony\Component\Console\Input\InputInterface;use Symfony\Component\Console\Output\OutputInterface;#[AsCommand(    name: 'tenant:database:create',    description: 'Proxy to create a new tenant database.',)]final class CreateDatabaseCommand extends Command{    public function __construct(        private DbService $dbService)    {        parent::__construct();    }    protected function configure()    {        $this            ->setDescription('Create a new database for a tenant')            ->setAliases(['t:d:c'])            ->addArgument('dbName', InputArgument::REQUIRED, 'Tenant Db name to create .')            ->setHelp('This command allows you to create a new database for a tenant');    }    protected function execute(InputInterface $input, OutputInterface $output): int    {        try {            $this->dbService->createDatabase($input->getArgument('dbName'));        } catch (\Exception $e) {            $output->writeln($e->getMessage());            return 1;        }        $output->writeln('Database created successfully');        return 0;    }}